import { Component, Input, OnInit } from '@angular/core';
import { ActivatedRoute, Params } from '@angular/router';
import { API } from '../../../../api';
import { DataService } from '../../../data.service';
import { ICommunity, ICommunityPost, IUserProfile } from '../../../interfaces';

@Component({
  selector: 'app-post-view',
  templateUrl: './post-view.component.html',
  styleUrls: ['./post-view.component.css'],
})
export class PostViewComponent implements OnInit {
  @Input() public post_id: string;
  @Input() public post: ICommunityPost;
  @Input() public group: ICommunity;

  private dummy_text: String =
    'Lorem ipsum dolor sit amet, consectetur adipiscing elit, sed do eiusmod tempor incididunt ut labore et dolore magna aliqua. Ut enim ad minim veniam, quis nostrud exercitation ullamco laboris nisi ut aliquip ex ea commodo consequat. Duis aute irure dolor in reprehenderit in voluptate velit esse cillum dolore eu fugiat nulla pariatur. Excepteur sint occaecat cupidatat non proident, sunt in culpa qui officia deserunt mollit anim id est laborum.';

  constructor(
    public dataService: DataService,
    public activatedRoute: ActivatedRoute
  ) {}

  ngOnInit(): void {
    if (!this.post_id) {
      this.activatedRoute.params.subscribe((params: Params) => {
        this.post_id = params['id'];
        this.refresh();
      });
    }
  }

  refresh(): void {
    this.dataService.fillGetPost(this.post_id).subscribe((result) => {
      this.post = result;
    });
  }

  populateDummyPost() {
    const posting_user = {
      name: 'Dheer Banker',
      profile_pic:
        'data:image/jpeg;base64,',
    } as IUserProfile;

    const dummy_content_size = this.dummy_text.length;

    this.post = {
      id: 'id',
      str_id: 'str_id',
      thread_rank: 1,
      content: this.dummy_text.slice(
        0,
        Math.floor(Math.random() * dummy_content_size)
      ),
      comments: [],
      comments_count: Math.floor(Math.random() * 100),
      posted_by: posting_user,
      time_of_creation: new Date(
        2010 + Math.floor(Math.random() * 20),
        1 + Math.floor(Math.random() * 12),
        1 + Math.floor(Math.random() * 28)
      ),
    } as ICommunityPost;

    this.dataService.FireGET<ICommunity[]>(API.Communities).subscribe(
      (result) => {
        this.group = result[0];
        this.dataService.setTitle(this.group.name);
      },
      (_) => {}
    );
  }
}
